document.addEventListener("DOMContentLoaded",function(){const n=document.querySelectorAll(".faq-item");n.forEach(t=>{t.addEventListener("click",function(e){if(!e.target.closest(".faq-answer")&&(n.forEach(o=>{o!==t&&o.classList.contains("active")&&o.classList.remove("active")}),t.classList.toggle("active"),t.classList.contains("active"))){const o=t.getBoundingClientRect(),c=o.bottom,s=window.innerHeight;if(c>s){const i=window.scrollY+o.top-100;window.scrollTo({top:i,behavior:"smooth"})}}})}),document.addEventListener("click",function(t){t.target.closest(".faq-item")||n.forEach(e=>{e.classList.remove("active")})}),document.querySelectorAll(".faq-answer").forEach(t=>{t.addEventListener("click",function(e){e.stopPropagation()})})});
