document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("postulacionForm");e&&e.addEventListener("submit",function(n){n.preventDefault();const t=e.querySelector(".submit-btn"),o=t.innerHTML;t.innerHTML='<span class="btn-text">Enviando...</span><span class="btn-icon">⏳</span>',t.disabled=!0,setTimeout(()=>{alert("¡Gracias por tu postulación! Nos pondremos en contacto contigo pronto."),e.reset(),t.innerHTML=o,t.disabled=!1},2e3)}),e.querySelectorAll("[required]").forEach(n=>{n.addEventListener("blur",function(){this.value.trim()?this.style.borderColor="rgba(202, 1, 1, 0.1)":this.style.borderColor="#E30613"})})});
